import React from "react";
import { motion } from "framer-motion";
import { cn } from "@/lib/utils";

interface CardProps {
  icon: React.ReactNode;
  title: string;
  description: string;
  className?: string;
}

const GRID_STRUCTURE = {
  columns: [
    { start: 0, width: 22.5 },
    { start: 25, width: 25 },
    { start: 50, width: 25 },
    { start: 75, width: 22.5 },
  ],
  rows: [
    { start: 0, height: 10 },
    { start: 10, height: 22.5 },
    { start: 32.5, height: 22.5 },
  ],
};

export default function CardHoverEffect({
  icon,
  title,
  description,
  className,
}: CardProps) {
  return (
    <motion.div
      whileHover={{ scale: 1.02 }}
      transition={{
        duration: 0.3,
        type: "keyframes",
        ease: "easeInOut",
      }}
      className={cn(
        "group relative z-30 cursor-pointer overflow-hidden rounded-2xl bg-black/5 p-8 pt-16",
        "before:absolute before:inset-0 before:rounded-[inherit] before:bg-gradient-to-b before:from-black/5 before:to-black/20 before:backdrop-blur-3xl before:content-['']",
        "after:absolute after:inset-0 after:rounded-[inherit] after:bg-gradient-to-b after:from-transparent after:via-transparent after:to-black/20 after:content-['']",
        "hover:shadow-[0px_5px_15px_rgba(0,0,0,0.06),0px_25px_35px_rgba(0,0,0,0.4),0_0_0_1.5px_rgba(255,255,255,0.2)]",
        "shadow-[0px_3px_8px_rgba(0,0,0,0.08),0px_12px_20px_rgba(0,0,0,0.15),0_0_0_1px_rgba(255,255,255,0.1)]",
        "dark:bg-white/[0.02] dark:before:from-black/5 dark:before:to-black/20",
        className
      )}
    >
      {/* Icon */}
      <motion.span
        className="relative z-50 table rounded-xl p-2.5"
        whileHover={{ scale: 1.1 }}
        transition={{
          duration: 0.3,
          type: "keyframes",
          ease: "easeInOut",
        }}
      >
        <span className="absolute inset-[4.5px] rounded-[inherit] border-[1.5px] border-white/10 bg-gradient-to-b from-white/10 to-white/5 backdrop-blur-3xl transition-all duration-300 group-hover:border-emerald-500/20 group-hover:from-emerald-500/10 group-hover:to-emerald-500/5" />
        <span className="relative z-[1] block text-zinc-400 transition-colors duration-300 group-hover:text-emerald-500">
          {icon}
        </span>
      </motion.span>

      {/* Content */}
      <h4 className="relative z-40 mb-2 mt-4 text-base font-semibold leading-tight tracking-tight text-white">
        {title}
      </h4>
      <p className="relative z-40 m-0 text-[15px] leading-relaxed text-zinc-400">
        {description}
      </p>

      {/* Shine Effect */}
      <div className="absolute inset-0 z-20 overflow-hidden rounded-[inherit] opacity-0 transition-all duration-500 group-hover:opacity-100">
        <div
          className="absolute bottom-[55%] left-1/2 aspect-square w-[200%] -translate-x-1/2 rounded-[50%]"
          style={{
            background: "conic-gradient(from 205deg at 50% 50%, transparent 0deg, hsl(160deg 95% 39%) 20deg, hsl(160deg 100% 85% / 0.3) 280deg, transparent 360deg)",
            filter: "blur(40px)",
          }}
        />
      </div>

      {/* Background Pattern */}
      <div className="absolute inset-0 overflow-hidden rounded-[inherit] [mask-image:radial-gradient(circle_at_70%_8%,black_0%,black_15%,transparent_60%)]">
        <div className="absolute inset-0">
          {/* Grid Lines */}
          <div className="duration-[350ms] absolute inset-0 opacity-0 transition-opacity group-hover:opacity-100">
            {/* Horizontal Lines */}
            {GRID_STRUCTURE.rows.map((row, i) => (
              <div
                key={`h-${i}`}
                className="duration-[350ms] absolute inset-x-0 h-[1px] origin-[0%_50%] scale-x-0 bg-gradient-to-r from-emerald-500/0 via-emerald-500/20 to-emerald-500/0 transition-transform group-hover:scale-x-100"
                style={{
                  top: `${row.start}%`,
                  transitionDelay: `${(2 - i) * 150}ms`,
                }}
              />
            ))}
            {/* Vertical Lines */}
            {GRID_STRUCTURE.columns.map((col, i) => (
              <div
                key={`v-${i}`}
                className="duration-[350ms] absolute inset-y-0 w-[1px] origin-[50%_0%] scale-y-0 bg-gradient-to-b from-emerald-500/0 via-emerald-500/20 to-emerald-500/0 transition-transform group-hover:scale-y-100"
                style={{
                  left: `${col.start}%`,
                  transitionDelay: `${(2 - i) * 150}ms`,
                }}
              />
            ))}
          </div>

          {/* Grid Cells */}
          <div className="group-hover:delay-[500ms] absolute inset-0 opacity-0 mix-blend-overlay transition-opacity duration-500 group-hover:opacity-100">
            {GRID_STRUCTURE.rows.map((row, rowIndex) => (
              <React.Fragment key={`row-${rowIndex}`}>
                {GRID_STRUCTURE.columns.map((col, colIndex) => (
                  <div
                    key={`cell-${rowIndex}-${colIndex}`}
                    className="absolute animate-tile bg-gradient-to-br from-emerald-500/20 to-emerald-500/0 opacity-0"
                    style={{
                      top: `${row.start}%`,
                      left: `${col.start}%`,
                      width: `${col.width}%`,
                      height: `${row.height}%`,
                      animationDelay: `${((rowIndex + colIndex) % 3) * 2}s`,
                    }}
                  />
                ))}
              </React.Fragment>
            ))}
          </div>
        </div>
      </div>
    </motion.div>
  );
}
