import React, { useState, useRef, useEffect } from 'react'
import { cn } from '@/lib/utils'

export interface FlipBookProps extends React.HTMLAttributes<HTMLDivElement> {
  pages: string[] // Array of image URLs or content for each page
  width?: number
  height?: number
  initialPage?: number
}

export default function FlipBook({
  pages,
  width = 400,
  height = 600,
  initialPage = 0,
  className,
  ...props
}: FlipBookProps) {
  const [currentPage, setCurrentPage] = useState(initialPage)
  const bookRef = useRef<HTMLDivElement>(null)
  const [isFlipping, setIsFlipping] = useState(false)

  const nextPage = () => {
    if (currentPage < pages.length - 1 && !isFlipping) {
      setIsFlipping(true)
      setCurrentPage(prev => prev + 1)
      setTimeout(() => setIsFlipping(false), 1000)
    }
  }

  const previousPage = () => {
    if (currentPage > 0 && !isFlipping) {
      setIsFlipping(true)
      setCurrentPage(prev => prev - 1)
      setTimeout(() => setIsFlipping(false), 1000)
    }
  }

  return (
    <div
      className={cn(
        "relative perspective-[1000px]",
        className
      )}
      style={{ width, height }}
      {...props}
    >
      <div
        ref={bookRef}
        className={cn(
          "relative w-full h-full transition-transform duration-1000",
          isFlipping && "transform-style-3d"
        )}
      >
        {/* Current Page */}
        <div
          className="absolute w-full h-full bg-white shadow-lg"
          style={{
            backgroundImage: `url(${pages[currentPage]})`,
            backgroundSize: 'cover',
            backfaceVisibility: 'hidden',
          }}
        />

        {/* Next Page */}
        {currentPage < pages.length - 1 && (
          <div
            className="absolute w-full h-full bg-white shadow-lg"
            style={{
              backgroundImage: `url(${pages[currentPage + 1]})`,
              backgroundSize: 'cover',
              backfaceVisibility: 'hidden',
              transform: 'rotateY(180deg)',
            }}
          />
        )}
      </div>

      {/* Navigation Controls */}
      <div className="absolute bottom-4 left-0 right-0 flex justify-center gap-4">
        <button
          onClick={previousPage}
          disabled={currentPage === 0}
          className="px-4 py-2 bg-primary text-primary-foreground rounded-md disabled:opacity-50"
        >
          Previous
        </button>
        <button
          onClick={nextPage}
          disabled={currentPage === pages.length - 1}
          className="px-4 py-2 bg-primary text-primary-foreground rounded-md disabled:opacity-50"
        >
          Next
        </button>
      </div>
    </div>
  )
}
